IF NOT EXISTS(SELECT 1 FROM T0010_EMAIL_FORMAT_SETTING WITH (NOLOCK) WHERE CMP_ID=@CMP_ID AND EMAIL_TYPE='Last Absenteeism Reminder')
		BEGIN
			SELECT @EMAIL_TYPE_ID=ISNULL(MAX(EMAIL_TYPE_ID),0) + 1 FROM T0010_EMAIL_FORMAT_SETTING WITH (NOLOCK)
			
